CREATE TABLE `gsxrest`.`token` ( `userEmail` VARCHAR(320) NOT NULL , `activationToken` VARCHAR(36) NULL DEFAULT NULL , `isActivationTokenConsumed` BOOLEAN NOT NULL DEFAULT FALSE , `authToken` VARCHAR(36) NULL DEFAULT NULL , `authTokenCreatedTs` TIMESTAMP NULL DEFAULT NULL , `authTokenLastUsedTs` TIMESTAMP NULL DEFAULT NULL , PRIMARY KEY (`userEmail`), UNIQUE (`authToken`)) ENGINE = MyISAM;
CREATE TABLE `gsxrest`.`requestLog` ( `id` INT NOT NULL AUTO_INCREMENT , `ts` TIMESTAMP NULL DEFAULT NULL, `gsxUserEmail` VARCHAR(320) NOT NULL , `endpoint` VARCHAR(254) NULL DEFAULT NULL , `httpCode` INT NULL DEFAULT NULL , `curlErrorNo` INT NULL DEFAULT NULL , `curlError` TEXT NULL DEFAULT NULL , `headers` TEXT NULL DEFAULT NULL , `requestBody` TEXT NULL DEFAULT NULL, `responseHeaders` TEXT NULL DEFAULT NULL , `response` TEXT NULL DEFAULT NULL , PRIMARY KEY (`id`)) ENGINE = InnoDB;